import { useState } from "react";
import  BlogList from "./BlogList";

const Home = () => {
    //Each object represent a blog
    const [blogs, setBlogs] = useState([
        {title:'New blog site1', body:'aaaaaaa', author:'Avihay', id:1},
        {title:'New blog site2', body:'bbbbbbb', author:'Noy', id:2},
        {title:'New blog site3', body:'ccccccc', author:'Noy', id:3},
        {title:'New blog site4', body:'ddddddd', author:'Avihay', id:4}
    ]);

    const handleDelete = (id) => {
        const newBlogs = blogs.filter(blog => blog.id !== id);
        setBlogs(newBlogs);
    }

    return ( 
        <div className="home">
            <BlogList blogs = {blogs} title="All Blogs" />
            <BlogList blogs = {blogs.filter((blog) => blog.author === "Noy")} title="Noy's Blogs" handleDelete={handleDelete}/>
            <BlogList blogs = {blogs.filter((blog) => blog.author === "Avihay")} title="Avihay's Blogs" handleDelete={handleDelete}/>
        </div>
     );
}

export default Home;
